mapper.select:

SELECT tlm_id AS id, tlm_name AS name, tlm_grade AS grade , tlm_type AS type 
FROM tl_member
WHERE tlm_id=#{id} AND tlm_pwd=#{pwd}

mapper.insert:
INSERT INTO tl_member( tlm_id, tlm_pwd, tlm_name, tlm_email, tlm_birth, tlm_terms
) VALUES ( #{tlm_id}, #{tlm_pwd}, #{tlm_name}, #{tlm_email}, #{tlm_birth}, #{tlm_terms}
)
